//Laptop L 1024px
$screen-lg: 1024px;
@mixin lg {
	@media (max-width: #{$screen-lg}) {
		@content;
	}
}
//Laptop L end

//Tablet - Landscape 768px
$screen-tb: 768px;
@mixin tb {
	@media (max-width: #{$screen-tb}) {
		@content;
	}
}
//Tablet - Landscape end

//Mobile L 425px
$screen-ml: 425px;
@mixin ml {
	@media (max-width: #{$screen-ml}) {
		@content;
	}
}
//Mobile L end

//Mobile m 375px
$screen-mm: 375px;
@mixin mm {
	@media (max-width: #{$screen-mm}) {
		@content;
	}
}
//Mobile s end

//Mobile s 425px
$screen-ms: 320px;
@mixin ms {
	@media (max-width: #{$screen-ms}) {
		@content;
	}
}
//Mobile s end

sources.scss/header.scss:
nav {
	background-color: $white;
	position: fixed;
	display: flex;
	top: 0;
	left: 0;
	z-index: 999;
	width: 100vw;
	padding: 1.7em 2em;
	height: 1.5em;

	@include ms {
		padding: 1.7em 1em;
	}

	h2 {
		font-family: $font;
		font-weight: $bold;
	}
}

#toggle {
	position: relative;
	cursor: pointer;
	display: flex;
	justify-content: center;
	align-items: center;
	margin-left: auto;
	margin-right: 5em;
	font-family: $font;

	@include ms {
		margin-right: 2em;
	}

	&::before {
		content: 'M';
		font-size: 1.4em;
		position: absolute;
		left: -1em;
		margin-top: .1em;
		opacity: .5;

		@include ms {
			display: none;
		}
	}

	&::after {
		content: 'NU';
		font-size: 1.4em;
		position: absolute;
		right: -1.6em;
		margin-top: .1em;
		opacity: .5;
		@include ms {
			display: none;
		}
	}

	.button_container {
		position: relative;
		height: 17px;
		width: 25px;
		transition: opacity .25s ease;
		display: flex;

		span {
			background: #000;
			border: none;
			height: 3px;
			border-radius: 4px;
			width: 100%;
			position: absolute;
			top: 0;
			left: 0;
			transition: transform .3s cubic-bezier(.4, 2.08, .55, .44), opacity .25s ease;
			transform: translate3d(0, 0, 0) rotate(0);
			cursor: pointer;

			@include tb {
				pointer-events: none;
			}

			&:nth-of-type(2) {
				top: 7px;
				transform: scaleX(0.6);
			}
			&:nth-of-type(3) {
				top: 14px;
				transform: scaleX(0.8);
			}
		}
		&:hover span {
			transform: scaleX(1);
		}

		&.menu-open span:nth-of-type(1) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #444;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 0;
			transform: translate3d(0, 7px, 0) rotate(45deg);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;
			transition-timing-function: cubic-bezier(.4, 2.08, .55, .44), ease;
			width: 100%;
		}

		&.menu-open span:nth-of-type(2) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #fff;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 7px;
			transform: scaleX(.6);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;
			transition-timing-function: cubic-bezier(.4, 2.08, .55, .44), ease;
			width: 100%;
			opacity: 0;
		}

		&.menu-open span:nth-of-type(3) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #444;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 0;
			transform: translate3d(0, 7px, 0) rotate(-45deg);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;

			width: 100%;
		}
	}
}

ul.menu {
	display: grid;
	position: fixed;
	justify-items: center;
	align-items: center;
	margin-left: auto;
	list-style: none;
	// margin-right: 4em;
	margin-top: 1.5em;
	padding-top: 3em;
	height: 100vh;
	width: 100%;
	background: $white;
	z-index: 99;
	transition: .25s ease-in-out;
	transition-property: transform;
	transition-duration: .5s;
	transition-timing-function: ease-in-out;
	transform: translate(100%, -100%) scale(.4);
text-align: center;
	&.open {
		transform: translate(0%) scale(1);
	}


	li {
		color: $primary-text;
		margin-bottom: 1em;
		font-family: $font;
		font-weight: $bold;
		font-size: $responsive__font;
		--max-font: 50;
		--min-font: 30;

		span {
			background-position: 0 100%;
			transition: background-position 0.5s;

			&:hover {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
				cursor: pointer;
			}

			&.active {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
			}
		}
	}
}

sources.scss/introduction.scss:
#introduction {
	padding-top: 5em;
	width: 100vw - 20vw;
	margin: auto;
	display: grid;
	justify-items: center;
	align-items: center;
	height: auto;
	min-height: 80vh;

	@include tb {
		min-height: 60vh;
	}

	@include ml {
		min-height: 40vh;
	}

	@include mm {
		min-height: 0;
	}

	.content {
		width: 100%;
		margin: auto;
		// letter-spacing: -.3em;

		grid-row: 2;
		line-height: 1.1;

		h1 {
			font-family: $font;
			font-weight: $regular;
			font-size: $responsive__font;
			--max-font: 95;
			--min-font: 45;

			span {
				font-weight: $bold;
			}

			.half-bg {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
				font-weight: $regular;
			}
		}
	}
}

.button {
	display: inline-block;
	text-decoration: none;
	position: relative;
	margin-top: 5vw;
	width: calc((var(--min-font) * 1px) + (var(--max-font) - var(--min-font)) * ((100vw - 420px) / (1200 - 420)));
	--max-font: 177;
	--min-font: 100;

	@include lg {
		width: 30%;
	}

	@include ml {
		margin-top: 7vw;
		width: 50%;
	}

	@include ms {
		margin-top: 7vw;
		width: 60%;
	}
}

.button .bottom {
	position: absolute;
	left: 7px;
	top: 7px;
	width: 100%;
	height: 100%;
	background-color: #fdd104;
	display: block;
	-webkit-transition: all .15s ease-out;
	-moz-transition: all .15s ease-out;
	-o-transition: all .15s ease-out;
	transition: all .15s ease-out;
}

.button .top {
	position: relative;
	left: 0;
	top: 0;
	padding: 24px 34px 22px 34px;
	border: 2px solid rgba(black, .5);

	@include ml {
		padding: 14px 14px 12px 14px;
	}
}

.button-dark .top {
	border: 2px solid #fff;
}

.button .top .label {
	font-family: sans-serif;
	font-weight: 600;
	color: black;
	font-size: 12px;
	line-height: 110%;
	letter-spacing: 2px;
	text-align: center;
	text-transform: uppercase;
	-webkit-transition: all .15s ease-out;
	-moz-transition: all .15s ease-out;
	-o-transition: all .15s ease-out;
	transition: all .15s ease-out;
}

.button-dark .top .label {
	color: #fff;
}

.button:hover .bottom {
	left: 0;
	top: 0;
	background-color: #fdd104;
}

.button:hover .top .label {
	color: black;
}

.button-border {
	position: absolute;
	background-color: #fdd104;
	-webkit-transition: all .25s ease-out;
	-moz-transition: all .25s ease-out;
	-o-transition: all .25s ease-out;
	transition: all .25s ease-out;
}

.button:hover .top .button-border-left,
.button:hover .top .button-border-right {
	height: calc(100% + 2px);
}

.button:hover .top .button-border-top,
.button:hover .top .button-border-bottom {
	width: calc(100% + 2px);
}

.button-border-left {
	left: -2px;
	bottom: -2px;
	width: 2px;
	height: 0;
}

.button-border-top {
	left: -2px;
	top: -2px;
	width: 0;
	height: 2px;
}

.button-border-right {
	right: -2px;
	top: -2px;
	width: 2px;
	height: 0;
}

.button-border-bottom {
	right: -2px;
	bottom: -2px;
	width: 0;
	height: 2px;
}

.fa-chevron-down {
	grid-row: 7;
	font-size: 1.8em;
	justify-self: center;
	animation-name: scrollAnimation;
	animation-duration: 2s;
	animation-iteration-count: infinite;
	animation-timing-function: ease-in-out;
	margin-top: 1em;
	@include ml {
display:none;	}
}

@keyframes scrollAnimation {
	50% {
		transform: translateY(-12px);
	}
}

